window.onscroll=function(){scrollFunction()};var navbar=document.getElementById("navbar");var wigarLogo=document.getElementsByClassName("wigar-logo")[0];var sticky=navbar.offsetTop;var scrollButton=document.getElementsByClassName("scroll-btn")[0];if(sticky===0){sticky=navbar.parentElement.offsetTop}function scrollFunction(){if(document.body.scrollTop>50||document.documentElement.scrollTop>50){scrollButton.classList.add("active")}else{scrollButton.classList.remove("active")}if(window.pageYOffset>=sticky){navbar.classList.add("sticky");wigarLogo.classList.remove("wigar-logo");wigarLogo.classList.add("wigar-logo-small");scrollButton.classList.add("active")}else{navbar.classList.remove("sticky");wigarLogo.classList.remove("wigar-logo-small");wigarLogo.classList.add("wigar-logo");scrollButton.classList.remove("active")}}function scrollToTop(){const c=document.documentElement.scrollTop||document.body.scrollTop;if(c>0){window.requestAnimationFrame(scrollToTop);window.scrollTo(0,c-c/10)}}